jQuery(document).ready( function($) {
        var val           = "";
        var checkbox  = $(".cp-checkbox[type=checkbox]");
        var className =  checkbox.attr("name");       
        var input     = $("#"+className);
        checkbox.on("change",function(){
            $.each( checkbox, function(){
              val = "";
              var isChecked = jQuery(this).is(":checked");
              if( isChecked ) {
                val += jQuery(this).val()+"|";               
              }
            });
            val = val.slice(0,-1);    
            input.val(val);
            input.attr("value",val);
            input.trigger("change");
            jQuery(document).trigger('cp-checkbox-change',[input , val] );
        });
    
});